#include "Poem.h"int main() {  std::ifstream in("../EvgenyOnegin.txt", std::ios::binary | std::ios::ate);  std::ofstream out("../poem.txt");  int64_t file_size = in.tellg();  in.close();  char* ev_on = new char[file_size];  in.open("../EvgenyOnegin.txt");  in.read(ev_on, file_size);  in.close();  Poem poem(ev_on);  poem.makePoem();  out << poem;  out.close();  return 0;}